import * as React from 'react';

export type AccordionChildren = {
    type?: any;
}[] | {
    type?: any;
} | any[];

export type AccordionHeadingLevel = "h1" | "h2" | "h3" | "h3" | "h4" | "h5" | "h6";

export interface AccordionProps {
    /**
     * Optional / additional class name
     */
    className?: string;
    /**
     * Required / Accordion items
     */
    children: AccordionChildren;
    /**
     * Optional / Allow to invert the position of the border for each AccordionItem, by default it shows the border top
     */
    invertedBorder?: boolean;
    /**
     * Heading level that will have each AccordionTitle for semantic. By default will be h2
     */
    headingLevel?: AccordionHeadingLevel;
    /**
     * Index of the child that will be expanded by default
     */
    initialActiveItem?: number;
}

declare const Accordion: React.FC<AccordionProps>;

export default Accordion;

