import * as React from 'react';

export type AccordionItemHeadingLevel = "h1" | "h2" | "h3" | "h3" | "h4" | "h5" | "h6";

export interface AccordionItemProps {
    /**
     * Optional / additional class name
     */
    className?: string;
    /**
     * Optional / accordion item callback
     */
    callback?: (...args: any[])=>any;
    /**
     * Required / accordion item title
     */
    title: string;
    /**
     * Required / accordion item children
     */
    children: React.ReactNode;
    /**
     * Internal onClick function passed on by Accordion parent
     */
    onClick?: (...args: any[])=>any;
    /**
     * Internal current active index passed on by Accordion parent
     */
    currentIndex?: number;
    /**
     * Internal expanded status passed on by Accordion parent
     */
    expanded?: boolean;
    /**
     * Optional / Allow to invert the position of the border for each AccordionItem, by default it shows the border top
     */
    invertedBorder?: boolean;
    /**
     * Heading level that will have each AccordionTitle for semantic. By default will be h2
     */
    headingLevel?: AccordionItemHeadingLevel;
}

declare const AccordionItem: React.FC<AccordionItemProps>;

export default AccordionItem;

