import * as React from 'react';

export type ActionMenuListIconType = "vertical-small" | "vertical-big" | "horizontal-small" | "horizontal-big";

export type ActionMenuListDialogPosition = "top-left" | "top-right" | "top-center" | "bottom-left" | "bottom-right" | "bottom-center";

export interface ActionMenuListBottomSheet {
    heading: string;
    primaryAction?: {
        onClick: (...args: any[])=>any;
        label: string;
    };
    secondaryAction?: {
        onClick: (...args: any[])=>any;
        label: string;
    };
}

export interface ActionMenuListProps {
    /**
     * opens menu
     */
    isMenuOpen?: boolean;
    /**
     * set is Menuopen
     */
    setIsMenuOpen: (...args: any[])=>any;
    /**
     * icon for the action menu list. Defaults to Kebab Icon
     */
    Icon?: any;
    /**
     * kebab icon specification, horizontal or vertical, size 32(big) or 24(small), optional as default is set
     */
    iconType?: ActionMenuListIconType;
    /**
     * whether the action menu list is within the header component.
     */
    inHeader?: boolean;
    /**
     * additional classname for component
     */
    className?: string;
    /**
     * position of the menu dialog in respect to kebab icon, desktop, optional
     */
    dialogPosition?: ActionMenuListDialogPosition;
    /**
     * Action menu list items
     */
    children: React.ReactNode;
    /**
     * bottom sheet props for small screens
     */
    bottomSheet: ActionMenuListBottomSheet;
}

declare const ActionMenuList: React.FC<ActionMenuListProps>;

export default ActionMenuList;

