import * as React from 'react';

export interface ActionMenuListItemLink {
    href: string;
    component?: React.ReactNode;
}

export interface ActionMenuListItemProps {
    /**
     * List item children
     */
    children: React.ReactNode;
    /**
     * onClick call back function
     */
    onClick?: (...args: any[])=>any;
    /**
     * adds custom class to list item
     */
    className?: string;
    /**
     * checks for lgviewort, calculated internally by Actionmenulist
     */
    isLgViewport?: boolean;
    /**
     * icon for the button
     */
    icon: (...args: any[])=>any;
    /**
     * if the menu item is link
     */
    link?: ActionMenuListItemLink;
}

declare const ActionMenuListItem: React.FC<ActionMenuListItemProps>;

export default ActionMenuListItem;

