import * as React from 'react';

export type AlertBannerType = "info" | "alert" | "success" | "error" | "new";

export interface AlertBannerCloseButton {
    onClick?: (...args: any[])=>any;
    label?: string;
    className?: string;
}

export interface AlertBannerGridMargins {
    xsMargin?: any;
    smMargin?: any;
    mdMargin?: any;
}

export interface AlertBannerProps {
    /**
     * Node inside AlertBanner
     */
    children: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Alert Banner type
     */
    type: AlertBannerType;
    /**
     * Alert Banner (info | alert | success) can be dismissed by providing closeButton props
     */
    closeButton?: AlertBannerCloseButton;
    title?: string;
    /**
     * Allows to align the title and content in the same line or separated lines when is true  *
     */
    separatedLines?: boolean;
    /**
     * text when type is used
     */
    statusBadgeText?: string;
    /**
     * Enables screen width variant of the component
     */
    isScreenWidthVariant?: boolean;
    /**
     * Grid margins for screen width variant
     */
    gridMargins?: AlertBannerGridMargins;
}

declare const AlertBanner: React.FC<AlertBannerProps>;

export default AlertBanner;

