import * as React from 'react';

export type AvatarSize = 32 | 42 | 48 | 60;

export interface AvatarProps {
    /**
     * Screen reader label
     */
    ariaLabel: string;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Notification badge count to appear if specified
     */
    count?: number;
    /**
     * The text representation of the avatar (ie: the name of the user)
     */
    text: string;
    /**
     * Image source provided for Avatar
     */
    image?: string;
    /**
     * Optional props passed to the notification badge
     */
    notificationBadgeProps?: Object;
    /**
     * Locale translated string for 'Notifications', used for NotificationBadge if used
     */
    notificationsText?: string;
    /**
     * Avatar specified sizes
     */
    size: AvatarSize;
    /**
     * disable avatar
     */
    disabled?: boolean;
}

declare const Avatar: React.FC<AvatarProps>;

export default Avatar;

