import * as React from 'react';

export interface BackButtonProps {
    /**
     * The text to appear in the button
     */
    children?: string;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * onClick callback on the button
     */
    onClick?: (...args: any[])=>any;
    /**
     * id on the inner button span. May be needed for some analytics solutions
     */
    spanId?: string;
}

declare const BackButton: React.FC<BackButtonProps>;

export default BackButton;

