import * as React from 'react';

export interface BackToTopProps {
    /**
     * Label text for screenreaders and to display if showLabel === true
     */
    label?: any;
    /**
     * Classnames can be added
     */
    className?: any;
    /**
     * Set the button's offset from bottom of the page (in px)
     */
    offsetBottom?: any;
    /**
     * onClick callback function
     */
    onClick?: any;
    /**
     * If true, displays the label underneath the button
     */
    showLabel?: any;
}

declare const BackToTop: React.FC<BackToTopProps>;

export default BackToTop;

