import * as React from 'react';

export interface BottomSheetPrimaryAction {
    onClick: (...args: any[])=>any;
    label: string;
}

export interface BottomSheetSecondaryAction {
    onClick: (...args: any[])=>any;
    label: string;
}

export interface BottomSheetProps {
    /**
     * Content inside of the bottom sheet
     */
    children: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Locale translation of close button label for accessibility
     */
    closeButtonLabel?: string;
    /**
     * Headline on the bottom sheet
     */
    headline?: string;
    /**
     * centers headline
     */
    centerHeadline?: boolean;
    /**
     * Sets if the bottom sheet is visible
     */
    isSheetVisible?: boolean;
    /**
     * Primary button action on the bottom sheet
     */
    primaryAction?: BottomSheetPrimaryAction;
    /**
     * Secondary button action on the bottom sheet
     */
    secondaryAction?: BottomSheetSecondaryAction;
    /**
     * Function to change the visibility bottom sheet state
     */
    setSheetVisible: (...args: any[])=>any;
    /**
     * Stacks the buttons
     */
    buttonsStacked?: boolean;
    /**
     * Adds a close button to the botton sheet header
     */
    withCloseButton?: boolean;
}

declare const BottomSheet: React.FC<BottomSheetProps>;

export default BottomSheet;

