import * as React from 'react';

export type BreadcrumbLinkComponent = ((...args: any[])=>any) | string | Object;

export interface BreadcrumbRouteMapArray {
    path: string;
    label: string | ((...args: any[])=>any) | React.ReactNode;
    linkClassName?: string;
}

export interface BreadcrumbProps {
    /**
     * An object with its keys representing the routes and its values representing the respective page names for the routes
     */
    routeNameMap?: Object;
    /**
     * React component, displayed as a separator between breadcrumb hierarchy, default to ">" icon
     */
    separator?: React.ReactElement<any>;
    /**
     * additional classes, applied to the breadcrumb component
     */
    className?: string;
    /**
     * displays a horizontal bottom ruler
     */
    showRuler?: boolean;
    /**
     * returns a translation for the text - "Back to ", defaults to English
     */
    backToTranslation?: (...args: any[])=>any;
    /**
     * the current pathname
     */
    pathname?: string;
    /**
     * What is rendered as the link element. Used mostly with react-router-dom Link component
     */
    linkComponent?: BreadcrumbLinkComponent;
    /**
     * An array of external route objects, with the key of each object representing the external route name and value of each
     * representing the callback function to be called on click of the respective external route. Shown in same order, as passed
     */
    routeMapArray?: BreadcrumbRouteMapArray[];
}

declare const Breadcrumb: React.FC<BreadcrumbProps>;

export default Breadcrumb;

