import * as React from 'react';

export type BuddyTipIconSize = 24 | 30 | 36 | 48;

export interface BuddyTipProps {
    /**
     * Text inside BuddyTip
     */
    children: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * An Icon to appear on the left
     */
    Icon: (...args: any[])=>any;
    /**
     * Optional iconSize
     */
    iconSize?: BuddyTipIconSize;
    /**
     * Optional the component to render for the BuddyTip text
     */
    textComponent?: string;
}

declare const BuddyTip: React.FC<BuddyTipProps>;

export default BuddyTip;

