import * as React from 'react';

export type CardType = "flatSolid" | "flatDashed" | "floatLow";

export type CardXsPadding = 18 | 24 | 30 | 36;

export type CardSmPadding = 18 | 24 | 30 | 36;

export type CardMdPadding = 18 | 24 | 30 | 36;

export type CardLgPadding = 18 | 24 | 30 | 36;

export interface CardProps {
    /**
     * Content inside the card
     */
    children?: React.ReactNode;
    /**
     * Optional / additional class name
     */
    className?: string;
    /**
     * Type of card to render
     */
    type?: CardType;
    /**
     * Card padding at extra small (xs) viewport
     */
    xsPadding?: CardXsPadding;
    /**
     * Card padding at small (sm) viewport
     */
    smPadding?: CardSmPadding;
    /**
     * Card padding at medium (md) viewport
     */
    mdPadding?: CardMdPadding;
    /**
     * Card padding at large (lg) viewport
     */
    lgPadding?: CardLgPadding;
}

declare const Card: React.FC<CardProps>;

export default Card;

