import * as React from 'react';

export type CarouselType = "single" | "multi" | "fade";

export interface CarouselText {
    heading?: string;
    body?: string;
}

export interface CarouselProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Type of the carousel
     */
    type: CarouselType;
    /**
     * Children wrapped inside carousel
     */
    children: React.ReactNode;
    /**
     * Optional looping of the carousel
     */
    loop?: boolean;
    text?: CarouselText[];
    /**
     * Optional initial index to display when the carousel loads
     */
    initialIndex?: number;
    /**
     * Optional arial label for previous and next button, default to be 'previous' and 'next
     */
    ariaLabelPrevious?: string;
    ariaLabelNext?: string;
    /**
     * Optional onChange callback
     * * @param {number} currentIndex: current tile index
     */
    onChange?: (...args: any[])=>any;
}

declare const Carousel: React.FC<CarouselProps>;

export default Carousel;

