import * as React from 'react';

export type CloseButtonIconSize = 14 | 18;

export interface CloseButtonProps {
    /**
     * Additional class name
     */
    className?: string;
    /**
     * Screenreader label for close button
     */
    closeButtonLabel: string;
    /**
     * Function that is called when button is clicked
     */
    onClick?: (...args: any[])=>any;
    /**
     * Set the icon size
     */
    iconSize?: CloseButtonIconSize;
    /**
     * adds a border to closebutton
     */
    withBorder?: boolean;
}

declare const CloseButton: React.FC<CloseButtonProps>;

export default CloseButton;

