import * as React from 'react';

export type DatePickerInputDateFormat =
    "DD/MM/YYYY"
    | "DD/MM/AAAA"
    | "JJ/MM/AAAA"
    | "MM/DD/YYYY"
    | "MM/DD/AAAA"
    | "MM/JJ/AAAA"
    | "YYYY/MM/DD"
    | "AAAA/MM/DD"
    | "AAAA/MM/JJ";

export type DatePickerDisallowedDates = Date[] | ((...args: any[]) => any);

export type DatePickerPosition = "top" | "bottom";

export type localeFormat = "en-CA" | "fr-CA" | "es-MX" | "es-CO" | "es-CL" | "es-PE";

export interface FieldError {
    label: string;
    messages: string[];
}

export interface Override {
    /**
     * @description default value
     * en-CA: 'Select date'
     * fr-CA: 'Sélectionner la date'
     * es-MX, es-CO, es-CL, es-PE: 'Seleccione Fecha'
     */
    mobileHeaderTitle: string,
    /**
     * @description default values
     * en-CA: ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December']
     * fr-CA: ['janvier', 'février', 'mars', 'avril', 'mai', 'juin', 'juillet', 'août', 'septembre', 'octobre', 'novembre', 'décembre']
     * es-MX, es-CO, es-CL, es-PE: ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre']
     */
    monthLabels: string[],
    /**
     * @description default values
     * en-CA: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec']
     * fr-CA: ['Jan', 'Fev', 'Mar', 'Avr', 'Mai', 'Jun', 'Jul', 'Aou', 'Sep', 'Oct', 'Nov', 'Dec']
     * es-MX, es-CO, es-CL, es-PE: ['Ene', 'Feb', 'Mar', 'Abr', 'May', 'Jun', 'Jul', 'Ago', 'Sep', 'Oct', 'Nov', 'Dic']
     */
    shortMonthLabels: string[],
    /**
     * @description default values
     * en-CA: ['S', 'M', 'T', 'W', 'T', 'F', 'S']
     * fr-CA: ['d', 'l', 'm', 'm', 'j', 'v', 's']
     * es-MX, es-CO, es-CL, es-PE: ['D', 'L', 'M', 'M', 'J', 'V', 'S']
     */
    weekdayLabels: string[],
    /**
     * @description default values
     * en-CA: ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']
     * fr-CA: ['Dimanche', 'Lundi', 'Mardi', 'Mercredi', 'Jeudi', 'Vendredi', 'Samedi']
     * es-MX, es-CO, es-CL, es-PE: ['Domingo', 'Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado']
     */
    daysOfTheWeek: string[],
    /**
     * @description default values
     * en-CA: ['First', 'Second', 'Third', 'Fourth', 'Fifth', 'Sixth', 'Seventh', 'Eight', 'Nineth', 'Tenth', 'Eleventh', 'Twelfth', 'Thirtinth', 'Fourteenth', 'Fifteenth', 'Sixteenth', 'Seventeenth', 'Eigteenth', 'Nineteenth', 'Twentieth', 'Twenty first', 'Twenty second', 'Twenty third', 'Twenty fourth', 'Twenty fifth', 'Twenty sixth', 'Twenty seventh', 'Twenty eighth', 'Twenty nineth', 'Thirtieth', 'Thirty first']
     * fr-CA: ['une', 'deux', 'trois', 'quatre', 'cinq', 'six', 'sept', 'huit', 'neuf', 'dix', 'onze', 'douze', 'treize', 'quatorze', 'quinze', 'seize', 'dix-huit', 'dix-huit', 'dix-neuf', 'vingt', 'vingt-et-un', 'vingt-deux', 'vingt trois', 'vingt quatre', 'vingt cinq', 'vingt-six', 'vingt sept', 'vingt-huit', 'vingt-neuf', 'trente', 'trente et un']
     * es-MX, es-CO, es-CL, es-PE: ['uno', 'dos', 'tres', 'cuatro', 'cinco', 'seis', 'siete', 'ocho', 'nueve', 'diez', 'once', 'doce ', 'trece', 'catorce', 'quince', 'dieciséis', 'diecisiete', 'dieciocho', 'diecinueve', 'Veinte', 'Veintiuno', 'veintidós', 'Veintitrés', 'veinticuatro', 'Veinticinco ', 'veintiséis', 'veintisiete', 'Veintiocho', 'Veintinueve', 'Treinta', 'Treinta y uno']
     */
    dayOfMonthLabel: string[],
    /**
     * @description default value
     * en-CA: 'Select date'
     * fr-CA: 'Sélectionnez la date'
     * es-MX, es-CO, es-CL, es-PE: 'Seleccione fecha'
     */
    startDateLabel: string,
    /**
     * @description default value
     * en-CA: 'Select end date'
     * fr-CA: 'Sélectionnez la date de fin'
     * es-MX, es-CO, es-CL, es-PE: 'Seleccione fecha final'
     */
    endDateLabel: string,
    /**
     * @description default value
     * en-CA: 'Cancel'
     * fr-CA: 'Annuler'
     * es-MX, es-CO, es-CL, es-PE: 'Cancelar'
     */
    clearDatesButtonLabel: string,
    /**
    * @description default value
    * en-CA: 'Set'
    * fr-CA: 'Fixer'
    * es-MX, es-CO, es-CL, es-PE: 'Establecer'
    */
    confirmDatesButtonLabel: string,
    /**
    * @description default value
    * en-CA: 'next month'
    * fr-CA: 'le mois prochain'
    * es-MX, es-CO, es-CL, es-PE: 'próximo mes'
    */
    ariaLabelNextMonth: string,
    /**
    * @description default value
    * en-CA: 'previous month'
    * fr-CA: 'le mois précédent'
    * es-MX, es-CO, es-CL, es-PE: 'mes anterior'
    */
    ariaLabelPreviousMonth: string,
    /**
     * @description default value
     * en-CA: 'close'
     * fr-CA: 'fermer'
     * es-MX, es-CO, es-CL, es-PE: 'cerca'
     */
    ariaLabelClose: string,
    /**
     * @description default value
     * en-CA: 'select month'
     * fr-CA: 'sélectionnez un mois'
     * es-MX, es-CO, es-CL, es-PE: 'seleccione mes'
     */
    ariaLabelSelectMonth: string,
    /**
     * @description default value
     * en-CA: 'select year'
     * fr-CA: 'sélectionnez l\'année'
     * es-MX, es-CO, es-CL, es-PE: 'seleccione año'
     */
    ariaLabelSelectYear: string,
    /**
     * @description default value
     * en-CA: 'row'
     * fr-CA: 'rangée'
     * es-MX, es-CO, es-CL, es-PE: 'fila'
     */
    row: string,
    /**
     * @description default value
     * en-CA: 'column'
     * fr-CA: 'colonne'
     * es-MX, es-CO, es-CL, es-PE: 'columna'
     */
    column: string,
    /**
     * @description default value
     * en-CA: 'is selected'
     * fr-CA: 'sélectionnée'
     * es-MX, es-CO, es-CL, es-PE: 'seleccionada'
     */
    isSelected: string,
    /**
     * @description default values
     * en-CA: ['one', 'two', 'three', 'four', 'five', 'six', 'seven', 'eight']
     * fr-CA: ['un', 'deux', 'troix', 'quatre', 'cinq', 'six', 'sept', 'huit']
     * es-MX, es-CO, es-CL, es-PE: ['uno', 'dos', 'tres', 'cuatro', 'cinco', 'seis', 'seite', 'ocho']
     */
    numbers: string[]
}

export interface Overrides {
    'en-CA'?: Partial<Override>,
    'fr-CA'?: Partial<Override>,
    'es-MX'?: Partial<Override>,
    'es-CO'?: Partial<Override>,
    'es-CL'?: Partial<Override>,
    'es-PE'?: Partial<Override>
}

type noopFunction = (...args: any[]) => any;

export interface DatePickerProps {
    /**
     * an optional array of two dates that determines which dates in the DatePicker are able to be clicked on
     */
    allowedRange?: Date[];
    /**
     * className to pass down to component
     */
    className?: string;
    /**
     * the dates that are selected by default
     */
    defaultSelected?: Date;
    /**
     * allows component value to be controlled
     */
    selected?: Date;
    /**
     * When true, users will not be able to interact with the DatePicker
     */
    disabled?: boolean;
    /**
     * Sets the error state and the error messages that will be displayed (used for custom errors)
     */
    error?: FieldError;
    /**
     * unique id of component
     */
    id?: string;
    /**
     * The format that specifies how a user can enter a given date in the input text field
     */
    inputDateFormat?: DatePickerInputDateFormat;
    /**
     * an optional array of dates or a function which accepts a date and returns true if date is not allowed
     */
    disallowedDates?: DatePickerDisallowedDates;
    /**
     * the lower and upper limit of the months/years shown in the calendar
     */
    limits?: Date[];
    /**
     * callback function that fires when a user has selected and confirmed dates in the DatePicker modal
     */
    onChange?: noopFunction;
    /**
     * Position of the calendar when the DatePicker is opened
     */
    position?: DatePickerPosition;
    /**
     * callback function that fires when a user types on the textfield
     */
    onTextfieldChange?: noopFunction;
    /** 
     * triggered on date picker clear 
     * */
    onDatePickerClear?: noopFunction,
    /** 
     * onBlur callback for date picker buttons 
     * */
    onBlur?: noopFunction,
    /**
     * locale to display
     */
    locale?: localeFormat;
    /**
     * overwrites the texts depending on the locale
     */
    overrides?: Overrides
}

declare const DatePicker: React.FC<DatePickerProps>;

export default DatePicker;
