import * as React from 'react';

export interface FileUploadFileSizeExceededError {
    label: string;
    msg: string;
}

export interface FileUploadTotalFilesSizeExceededError {
    label: string;
    msg: string;
}

export interface FileUploadProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Optional classes can be added
     */
    id: string;
    /**
     * Optional flag to disable file upload component
     */
    disabled?: boolean;
    /**
     * Optional label override for the upload button
     */
    label?: string;
    /**
     * The list of Files that this component will add to the upload
     */
    files: File[];
    /**
     * The function to update files list
     */
    setFiles: (...args: any[])=>any;
    /**
     * The size limit in BYTES for individual files
     */
    fileSizeLimit?: number;
    /**
     * The size limit in BYTES for all cumilative files
     */
    totalSizeLimit?: number;
    /**
     * A list of accepted file types as defined in https://developer.mozilla.org/en-US/docs/Web/HTML/Element/input/file#unique_file_type_specifiers
     */
    acceptedFileTypes?: string[];
    /**
     * Label to show when no files have been selected
     */
    noFilesSelectedLabel?: string;
    /**
     * Label to show when one or more files have been selected
     */
    filesSelectedLabel?: string;
    /**
     * File size exceeded error message
     */
    fileSizeExceededError?: FileUploadFileSizeExceededError;
    /**
     * Total files size exceeded error message
     */
    totalFilesSizeExceededError?: FileUploadTotalFilesSizeExceededError;
    removeFileLabel?: string;
    allowMultiple?: boolean;
}

declare const FileUpload: React.FC<FileUploadProps>;

export default FileUpload;

