import * as React from 'react';

export type FilterTooltip = React.ReactNode | Object;

export interface FilterProps {
    /**
     * The DOM nodes (options) to appear within the dropdown
     */
    children: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Optional default value set in the dropdown, based on the options inside
     */
    defaultValue?: string;
    /**
     * Optional controlled value
     */
    value?: string;
    /**
     * Optional id on the select element
     */
    id?: string;
    /**
     * An Icon to appear in the filter, on the left
     */
    Icon?: any;
    /**
     * Label to be associated with the filter dropdown (for screenreaders)
     */
    label: string;
    /**
     * displays the label when set to true
     */
    isLabelVisible?: boolean;
    /**
     * The name attribute on the select element
     */
    name: string;
    /**
     * onChange function for when the Filter is updated
     */
    onChange?: (...args: any[])=>any;
    /**
     * Optional secondary label displayed beside the primary label
     */
    secondaryLabel?: string;
    /**
     * If field needs more explanation, can include a tooltip node that will appear beside label
     */
    tooltip?: FilterTooltip;
    /**
     * Optional light background behind input
     */
    withBackground?: boolean;
}

declare const Filter: React.FC<FilterProps>;

export default Filter;

