import * as React from 'react';

export interface FooterLinks {
    link?: string;
    text?: string;
    target?: string;
}

export type FooterLogoSize = 24 | 36;

export type FooterCopyRightBackground = "white" | "grey";

export interface FooterProps {
    /**
     * Specify an optional className to apply to the underlying footer node
     */
    className?: string;
    /**
     * An array of links to be rendered by the Footer
     */
    links?: FooterLinks[];
    /**
     * Text to be rendered in the footer copyright section
     */
    copyRightText?: string;
    /**
     * Size of the logo
     */
    logoSize?: FooterLogoSize;
    /**
     * Background color of the bottom copyright bar
     */
    copyRightBackground?: FooterCopyRightBackground;
}

declare const Footer: React.FC<FooterProps>;

export default Footer;

