import * as React from 'react';

export type GridXsMargin = 18 | 24 | 30 | 36;

export type GridSmMargin = 24 | 30 | 36 | 42;

export type GridMdMargin = 36 | 42 | 48 | 54;

export interface GridProps {
    /**
     * The DOM content to appear inside of the column
     */
    children?: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Fluid grid does not have a max width
     */
    fluid?: boolean;
    xsMargin?: GridXsMargin;
    smMargin?: GridSmMargin;
    mdMargin?: GridMdMargin;
}

declare const Grid: React.FC<GridProps>;

export default Grid;

