import * as React from 'react';

export type HamburgerFocusColor = "blue" | "white";

export interface HamburgerProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Optional hamburger menu icon
     */
    Icon?: (...args: any[])=>any;
    /**
     * Optional hamburger menu icon size
     */
    iconSize?: number;
    /**
     * menu items
     */
    children: React.ReactNode;
    /**
     * label to be added to the menu button when the menu is open
     */
    menuButtonLabel: string;
    /**
     * label to be added to the menu button  when the menu is closed
     */
    closedMenuButtonLabel: string;
    /**
     * This label will be read by the screen reader when the menu is open
     */
    openMenuLabel: string;
    /**
     * opens menu when set to true
     */
    isMenuOpen: boolean;
    /**
     * function to update menu's current state, i.e open(true) or close(false)
     */
    setIsMenuOpen: (...args: any[])=>any;
    /**
     * hamburger icon color
     */
    iconColor?: string;
    /**
     * hamburger button focus color
     */
    focusColor?: HamburgerFocusColor;
    /**
     * header component styling
     */
    header?: boolean;
}

declare const Hamburger: React.FC<HamburgerProps>;

export default Hamburger;

