import * as React from 'react';

export type HamburgerLinkComponent = ((...args: any[])=>any) | string | Object;

export interface HamburgerLinkProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Specifies the URL destination if the link is clicked
     */
    href: string;
    /**
     * What is rendered as the parent element. Used mostly with react-router-dom Link component
     */
    component?: HamburgerLinkComponent;
    /**
     * Link text
     */
    children: React.ReactNode;
}

declare const HamburgerLink: React.FC<HamburgerLinkProps>;

export default HamburgerLink;

