import * as React from 'react';

export interface HeaderLogo {
    Icon: (...args: any[])=>any;
    logoDescription?: string;
    component?: React.ReactNode;
    href?: string;
}

export type HeaderType = "red" | "white" | "black";

export interface HeaderHelpLink {
    href: string;
    target: string;
}

export interface HeaderActionMenuList {
    type?: any;
}

export interface HeaderNavLinks {
    text: string;
    component?: ((...args: any[])=>any) | string | Object;
    isActive?: boolean;
    href: string;
    icon?: (...args: any[])=>any;
    onClick?: (...args: any[])=>any;
}

export interface HeaderSkipContent {
    linkText?: string;
    linkColor?: string;
    contentId: string;
}

export type HeaderDialogPosition = "top-left" | "top-right" | "top-center" | "bottom-left" | "bottom-right" | "bottom-center";

export type HeaderIconType = "vertical-small" | "vertical-big" | "horizontal-small" | "horizontal-big";

export interface HeaderProps {
    /**
     * unique id for the header
     */
    id: string;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Logo for the header
     */
    logo?: HeaderLogo;
    type?: HeaderType;
    /**
     * help link object
     */
    helpLink?: HeaderHelpLink;
    /**
     * pass an actionMenuList component to the header
     */
    actionMenuList?: HeaderActionMenuList;
    /**
     * nav bar links
     */
    navLinks?: HeaderNavLinks[];
    /**
     * skip content bar
     */
    skipContent?: HeaderSkipContent;
    /**
     * bottom sheet close or cancel button label
     */
    bottomSheetButtonLabel?: string;
    /**
     * sets a box-shadow for the header
     */
    shadow?: boolean;
    /**
     * position of the menu dialog in respect to kebab icon, desktop, optional
     */
    dialogPosition?: HeaderDialogPosition;
    /**
     * kebab icon specification, horizontal or vertical, size 32(big) or 24(small), optional as default is set
     */
    iconType?: HeaderIconType;
}

declare const Header: React.FC<HeaderProps>;

export default Header;

