import * as React from 'react';

export type IconSpinnerSize = 18 | 24 | 32 | 48 | 72;

export interface IconSpinnerProps {
    /**
     * Set the aria-label for the icon
     */
    accessibleTitle?: string;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Set the color of the icon
     */
    color?: any;
    /**
     * Set the size of the icon (in px)
     */
    size?: IconSpinnerSize;
}

declare const IconSpinner: React.FC<IconSpinnerProps>;

export default IconSpinner;

