import * as React from 'react';

export type InputSliderCurrencyFormat = "CAD-EN" | "CAD-FR" | "ES-MXN" | "ES-PEN" | "ES-COL" | "ES-CL";

export interface InputSliderCurrencyConfig {
    prefix?: string;
    suffix?: string;
    decimalSymbol: string;
    allowDecimal?: boolean;
    thousandsSeparatorSymbol: string;
    allowNegative?: boolean;
}

export interface InputSliderError {
    label: string;
    messages: string[];
}

export interface InputSliderSupplementaryTextLeft {
    heading: "h1" | "h2" | "h3" | "h4" | "h5" | "h6";
    label: string;
    value: string;
}

export interface InputSliderSupplementaryTextRight {
    heading: "h1" | "h2" | "h3" | "h4" | "h5" | "h6";
    label: string;
    value: string;
}

export interface InputSliderProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Optional input id
     */
    id?: string;
    /**
     * Optional input label
     */
    label?: string;
    /**
     * Optional input and slider value
     */
    defaultValue?: number;
    /**
     * Required min value
     */
    min?: any;
    /**
     * Required max value
     */
    max?: any;
    /**
     * Optional onChange function
     */
    onChange?: (...args: any[])=>any;
    /**
     * Optional secondary label
     */
    secondaryLabel?: string;
    /**
     * Optional disabled property
     */
    disabled?: boolean;
    /**
     * Optional track step number forward or back
     */
    step?: number;
    /**
     * Optional aria-label for input
     */
    inputAriaLabel?: string;
    /**
     * Optional aria-label for slider
     */
    trackAriaLabel?: string;
    /**
     * Optional aria-valuetext for slider
     */
    trackAriaValueText?: string;
    /**
     * Currency format, currencyFormat prop will always override currencyConfig prop
     */
    currencyFormat?: InputSliderCurrencyFormat;
    /**
     * Optional config for custom currency
     */
    currencyConfig?: InputSliderCurrencyConfig;
    /**
     * Sets the error state and the error messages that will be displayed.
     */
    error?: InputSliderError;
    /**
     * Supplementary text to appear under the slider on the left
     */
    supplementaryTextLeft?: InputSliderSupplementaryTextLeft;
    /**
     * Supplementary text to appear under the slider on the right
     */
    supplementaryTextRight?: InputSliderSupplementaryTextRight;
}

declare const InputSlider: React.FC<InputSliderProps>;

export default InputSlider;

