import * as React from 'react';

export type LineSeparatorType = "solid" | "dashed";

export interface LineSeparatorProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Line type. Can be solid or dashed
     */
    type?: LineSeparatorType;
}

declare const LineSeparator: React.FC<LineSeparatorProps>;

export default LineSeparator;

