import * as React from 'react';

export type LoadingIndicatorSize = "extraSmall" | "small" | "medium" | "large" | "extraLarge";

export type LoadingIndicatorColor = "red" | "darkGray";

export interface LoadingIndicatorProps {
    /**
     * Set the aria-label for the icon
     */
    accessibleTitle?: string;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Set the size of the icon
     */
    size?: LoadingIndicatorSize;
    /**
     * Set the color of the icon
     */
    color?: LoadingIndicatorColor;
}

declare const LoadingIndicator: React.FC<LoadingIndicatorProps>;

export default LoadingIndicator;

