import * as React from 'react';

export type LogoScotiaColor = "red" | "white";

export type LogoScotiaSize = 12 | 18 | 24 | 30 | 36 | 42;

export interface LogoScotiaProps {
    /**
     * Color of logo
     */
    color?: LogoScotiaColor;
    /**
     * Size of logo
     */
    size?: LogoScotiaSize;
    /**
     * Unique id for the logo, required for accessibility / screenreaders
     */
    id: string;
    /**
     * Description that is read by screenreaders
     */
    logoDescription?: string;
}

declare const LogoScotia: React.FC<LogoScotiaProps>;

export default LogoScotia;

