import * as React from 'react';

export type MarginSide = "both" | "top" | "bottom";

export interface MarginProps {
    children: React.ReactNode;
    className?: string;
    /**
     * Extra Small Screen size - can be a number to control the top and/or bottom margin OR an array of up to 4 values following the CSS property margin: top right bottom left
     */
    xs?: any;
    /**
     * Small screen size - can be a number to control the top and/or bottom margin OR an array of up to 4 values following the CSS property margin: top right bottom left
     */
    sm?: any;
    /**
     * Medium Screen size - can be a number to control the top and/or bottom margin OR an array of up to 4 values following the CSS property margin: top right bottom left
     */
    md?: any;
    /**
     * Large screen size - can be a number to control the top and/or bottom margin OR an array of up to 4 values following the CSS property margin: top right bottom left
     */
    lg?: any;
    /**
     * Selects what sides of the component are affected by the margin, only used when the xs/sm/md/lg are numbers instead of arrays
     */
    side?: MarginSide;
}

declare const Margin: React.FC<MarginProps>;

export default Margin;

