import * as React from 'react';

export type MeterLabelTooltip = React.ReactNode | Object;

export interface MeterProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * label
     */
    label: string;
    /**
     * optional text in label
     */
    labelOptionalText?: string;
    /**
     * optional tooltip in label
     */
    labelTooltip?: MeterLabelTooltip;
    /**
     * max value of the meter (defaults to 1)
     */
    max?: number;
    /**
     * value
     */
    value?: number;
    /**
     * left/start subtitle
     */
    leftSupplementaryText?: string;
    /**
     * right/end subtitle
     */
    rightSupplementaryText?: string;
    /**
     * top right subtitle
     */
    topSupplementaryText?: string;
}

declare const Meter: React.FC<MeterProps>;

export default Meter;

