import * as React from 'react';

export interface MobilePaginationProps {
    /**
     * Set the id for the underlying div tag
     */
    id: string;
    /**
     * allback fired when the view more button is clicked
     * @param {number} page the new page number
     */
    onClick: (...args: any[])=>any;
    /**
     * The total number of results avaliable
     */
    totalResultCount: number;
    /**
     * Text for the view more button
     */
    buttonText: string;
    /**
     * optional function for localizing pagination text ,
     * @param {number} start Start index of the visible results
     * @param {number} end End index of the visible results
     * @param {number} totalResultCount The total number of results avaliable
     * @returns {string} Text caption for the pagination, example: 1-20 of 200 results
     */
    localizePaginationText?: (...args: any[])=>any;
    /**
     * The current page.
     */
    currentPage?: number;
    /**
     * Number of results per page
     */
    pageSize?: number;
    /**
     * Specify an optional className to apply to the underlying div tag
     */
    className?: string;
}

declare const MobilePagination: React.FC<MobilePaginationProps>;

export default MobilePagination;

