import * as React from 'react';

export type ModalAlertWidth = "narrow" | "wide";

export interface ModalAlertProps {
    /**
     * Text inside of ModalAlert
     */
    children: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Headline of the Alert
     */
    headline: string;
    /**
     * Define the visibility for the Modal
     */
    isModalVisible?: boolean;
    /**
     * Primary button label
     */
    primaryButtonLabel: string;
    /**
     * Primary Action
     */
    primaryAction: (...args: any[])=>any;
    /**
     * Function to change the visibility Modal state
     */
    setModalVisible: (...args: any[])=>any;
    /**
     * Dialog width. can be narrow or wide
     */
    width?: ModalAlertWidth;
}

declare const ModalAlert: React.FC<ModalAlertProps>;

export default ModalAlert;

