import * as React from 'react';

export interface MultiSelectError {
    label?: string;
    messages?: string[];
}

export interface MultiSelectOptions {
    name: string;
    label: string;
    value: string;
    disabled?: boolean;
    checked?: boolean;
    checkColor?: string;
}

export type MultiSelectTooltip = React.ReactNode | Object;

export type MultiSelectIcon = any | any;

export interface MultiSelectProps {
    /**
     * unique id for the component
     */
    id: string;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Error for MultiSelect
     */
    error?: MultiSelectError;
    label: string;
    /**
     * optional secondary label
     */
    secondaryLabel?: string;
    /**
     * Place holder text for the list
     */
    placeholder: string;
    /**
     * string to translate number of items selected
     */
    itemsSelectedLocale?: string;
    /**
     * opens the multi select popup when set to true
     */
    isMutliSelectOpen?: boolean;
    /**
     * Call back function when the multi-select button is clicked
     */
    setIsMultiSelectOpen?: (...args: any[])=>any;
    /**
     * options for the Multi-select
     */
    options: MultiSelectOptions[];
    /**
     * onChange callback of the options
     */
    onChange?: (...args: any[])=>any;
    /**
     * disable MultiSelect component
     */
    disabled?: boolean;
    /**
     * Bottomsheet options
     */
    bottomSheetCloseButtonlabel?: string;
    /**
     * If fieldneeds more explanation, can include a tooltip node that will appear beside label
     */
    tooltip?: MultiSelectTooltip;
    /**
     * Icon to replace the label
     */
    Icon?: MultiSelectIcon;
    /**
     * Optional light background behind input
     */
    withBackground?: boolean;
}

declare const MultiSelect: React.FC<MultiSelectProps>;

export default MultiSelect;

