import * as React from 'react';

export type NotificationBadgeComponent = ((...args: any[])=>any) | string | Object;

export type NotificationBadgeIconSize = 30 | 36 | 48;

export type NotificationBadgeTarget = "_self" | "_blank" | "_parent" | "_top" | "framename" | any;

export type NotificationBadgeTextSize = 12 | 14 | 16 | 18;

export type NotificationBadgeType = "default" | "list";

export interface NotificationBadgeProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * What is rendered as the container element. Used mostly with react-router-dom Link component
     */
    component?: NotificationBadgeComponent;
    /**
     * Specifies the URL destination if the notification badge with icon and/or text is clicked
     */
    href?: string;
    /**
     * Set to true to show the icon
     */
    showIcon?: boolean;
    /**
     * Size of the icon
     */
    iconSize?: NotificationBadgeIconSize;
    /**
     * Locale translated string for 'Notifications'
     */
    notificationsText: string;
    /**
     * The number to appear inside of the badge
     */
    number?: number;
    /**
     * Specifies where to open the linked document
     */
    target?: NotificationBadgeTarget;
    /**
     * Text label to be shown
     */
    text?: string;
    /**
     * Size of text label
     */
    textSize?: NotificationBadgeTextSize;
    /**
     * Type of notification badge
     */
    type?: NotificationBadgeType;
}

declare const NotificationBadge: React.FC<NotificationBadgeProps>;

export default NotificationBadge;

