import * as React from 'react';

export type OrderedListSize = 16 | 18;

export interface OrderedListProps {
    /**
     * The children within the ordered list
     */
    children?: React.ReactNode;
    /**
     * Set the font size. If not specified, it inherits from parent
     */
    size?: OrderedListSize;
    /**
     * Specify whether this ordered list is nested inside of another list
     */
    nested?: boolean;
    /**
     * Specify an optional className to apply to the underlying ol node
     */
    className?: string;
}

declare const OrderedList: React.FC<OrderedListProps>;

export default OrderedList;

