import * as React from 'react';

export type PillButtonType = "caution" | "default" | "inverted";

export type PillButtonInvertedFocusState = "yellow" | "blue";

export interface PillButtonProps {
    /**
     * The text to appear in the button
     */
    children?: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * No user action on button is allowed
     */
    disabled?: boolean;
    /**
     * onClick callback on the button
     */
    onClick?: (...args: any[])=>any;
    /**
     * Type of underlying button input
     */
    buttonType?: string;
    /**
     * Type of PillButton
     */
    type?: PillButtonType;
    /**
     * works only for inverted pill button
     */
    invertedFocusState?: PillButtonInvertedFocusState;
}

declare const PillButton: React.FC<PillButtonProps>;

export default PillButton;

