import * as React from 'react';

export type ProgressIndicatorValue = string | number;

export interface ProgressIndicatorProps {
    /**
     * Required id for Progress component
     */
    id: string;
    /**
     * Required value for bar completion from 0 to 100
     */
    value: ProgressIndicatorValue;
    /**
     * Required label
     */
    label: string;
    /**
     * Optional classes can be added
     */
    className?: string;
}

declare const ProgressIndicator: React.FC<ProgressIndicatorProps>;

export default ProgressIndicator;

