import * as React from 'react';

export type QuickActionCardComponent = string | Object;

export type QuickActionCardChevronColor = "blue" | "black";

export interface QuickActionCardProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Component to be used as the wrapper of the QuickActionCard (typically button, or link)
     */
    Component?: QuickActionCardComponent;
    /**
     * An Icon to appear on the left
     */
    Icon: (...args: any[])=>any;
    /**
     * Label to appear after the Icon
     */
    label: string;
    /**
     * Badge next to the label
     */
    badge?: React.ReactNode;
    /**
     * Skeleton animation to load before the dynamic value
     */
    showSkeleton?: boolean;
    /**
     * Dynamic value to show before the chevron
     */
    value?: string;
    /**
     * The color of the chevron
     */
    chevronColor?: QuickActionCardChevronColor;
    /**
     * Set to false if the card shadow styling is not desired
     */
    cardShadow?: boolean;
    /**
     * Set to true to use the stacked variant of the QuickActionCard
     */
    stacked?: boolean;
}

declare const QuickActionCard: React.FC<QuickActionCardProps>;

export default QuickActionCard;

