import * as React from 'react';

export type RadioButtonInputRef = ((...args: any[])=>any) | Object;

export type RadioButtonTooltip = React.ReactNode | Object;

export interface RadioButtonProps {
    /**
     * Set the checked state of the radio button for controlled component
     */
    checked?: boolean;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Set the RadioButton to the disabled state
     */
    disabled?: boolean;
    /**
     * Set the id of the input tag
     */
    id: string;
    /**
     * Set the label of the radio button
     */
    label: React.ReactNode;
    /**
     * Set the name of the input tag
     */
    name: string;
    /**
     * Ref to access dom node
     */
    inputRef?: RadioButtonInputRef;
    /**
     * Default state for uncontrolled radio button
     */
    defaultChecked?: boolean;
    /**
     * If radio button needs more explanation, can include a tooltip node that will appear beside label
     */
    tooltip?: RadioButtonTooltip;
    /**
     * Optional onChange function
     */
    onChange?: (...args: any[])=>any;
}

declare const RadioButton: React.FC<RadioButtonProps>;

export default RadioButton;

