import * as React from 'react';

export type RangeInputSliderCurrencyFormat = "CAD-EN" | "CAD-FR" | "ES-MXN" | "ES-PEN" | "ES-COL" | "ES-CL";

export interface RangeInputSliderCurrencyConfig {
    prefix?: string;
    suffix?: string;
    thousandsSeparatorSymbol: string;
}

export interface RangeInputSliderError {
    label: string;
    messages: string[];
}

export interface RangeInputSliderSupplementaryTextLeft {
    heading: "h1" | "h2" | "h3" | "h4" | "h5" | "h6";
    label: string;
    value: string;
}

export interface RangeInputSliderSupplementaryTextRight {
    heading: "h1" | "h2" | "h3" | "h4" | "h5" | "h6";
    label: string;
    value: string;
}

export interface RangeInputSliderProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Optional input id
     */
    id?: string;
    /**
     * Required range value, shape {min, max}
     */
    defaultRange?: any;
    /**
     * Required min value
     */
    min?: any;
    /**
     * Required max value
     */
    max?: any;
    /**
     * Optional onChange function
     */
    onChange?: (...args: any[])=>any;
    /**
     * Optional disabled property
     */
    disabled?: boolean;
    /**
     * Optional track step number forward or back
     */
    step?: number;
    /**
     * Optional secondary label
     */
    secondaryLabel?: string;
    /**
     * Optional label
     */
    label?: string;
    /**
     * Optional input aria label
     */
    inputAriaLabel?: string;
    /**
     * Optional track aria label
     */
    trackAriaLabel?: string;
    /**
     * Optional track aria value text
     */
    trackAriaValueText?: string;
    /**
     * Currency format, currencyFormat prop will always override currencyConfig prop
     */
    currencyFormat?: RangeInputSliderCurrencyFormat;
    /**
     * Optional config for custom currency
     */
    currencyConfig?: RangeInputSliderCurrencyConfig;
    /**
     * Sets the error state and the error messages that will be displayed.
     */
    error?: RangeInputSliderError;
    /**
     * Supplementary text to appear under the slider on the left
     */
    supplementaryTextLeft?: RangeInputSliderSupplementaryTextLeft;
    /**
     * Supplementary text to appear under the slider on the right
     */
    supplementaryTextRight?: RangeInputSliderSupplementaryTextRight;
}

declare const RangeInputSlider: React.FC<RangeInputSliderProps>;

export default RangeInputSlider;

