import * as React from 'react';

export type RequirementsHeader = "h1" | "h2" | "h3" | "h4" | "h5" | "h6";

export interface RequirementsProps {
    /**
     * RequirementsText elements
     */
    children: any[];
    /**
     * Locale translated string for 'Password requirements'
     */
    title?: string;
    /**
     * Type of HTML header element rendered for the title
     */
    header: RequirementsHeader;
    /**
     * The ID of the Requirements component, used for PasswordTextField screen reading
     */
    id: string;
}

declare const Requirements: React.FC<RequirementsProps>;

export default Requirements;

