import * as React from 'react';

export type RowJustifyContent = "start" | "center" | "end" | "spaceAround" | "spaceBetween";

export interface RowProps {
    /**
     * The DOM content to appear inside of the column
     */
    children?: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Specifes whether there are gutters between the columns
     */
    gutters?: boolean;
    /**
     * Determines how the content will be positioned along the flex axis
     */
    justifyContent?: RowJustifyContent;
    /**
     * Set to true if this row is nested within another row
     */
    nested?: boolean;
}

declare const Row: React.FC<RowProps>;

export default Row;

