import * as React from 'react';

export interface SROnlyProps {
    /**
     * The content that will be hidden from the page but announced by screenreaders
     */
    children?: React.ReactNode;
    /**
     * Type of HTML element that will contain the screen reader only content
     */
    component?: string;
}

declare const SROnly: React.FC<SROnlyProps>;

export default SROnly;

