import * as React from 'react';

export type SecondaryButtonLabelPadding = 36 | 30 | 24;

export type SecondaryButtonSize = "small" | "regular";

export interface SecondaryButtonProps {
    /**
     * The id for this component
     */
    id?: string;
    /**
     * The text to appear in the button
     */
    children?: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * No user action on button is allowed
     */
    disabled?: boolean;
    /**
     * Button onClick callback function
     */
    onClick?: (...args: any[])=>any;
    /**
     * Padding on the left and right of the button
     */
    labelPadding?: SecondaryButtonLabelPadding;
    /**
     * Size variant of the button
     */
    size?: SecondaryButtonSize;
}

declare const SecondaryButton: React.FC<SecondaryButtonProps>;

export default SecondaryButton;

