import * as React from 'react';

export type SideSheetType = "overlay" | "persistent";

export interface SideSheetProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Sets if the side sheet is visible
     */
    isSheetVisible: boolean;
    /**
     * Function to change the visibility bottom sheet state
     */
    setSheetVisible: (...args: any[])=>any;
    /**
     * type of the side sheet
     */
    type?: SideSheetType;
    /**
     * content inside the side sheet
     */
    children: React.ReactNode;
    /**
     * label for the close button
     */
    closeButtonLabel?: string;
    /**
     * heading for side sheet
     */
    heading: string;
}

declare const SideSheet: React.FC<SideSheetProps>;

export default SideSheet;

