import * as React from 'react';

export type SkeletonType = "text" | "circle" | "rectangular";

export type SkeletonHeight = any | any;

export type SkeletonWidth = any | any;

export type SkeletonAnimation = "pulse" | "wave" | any;

export interface SkeletonProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * type of the variant
     */
    type?: SkeletonType;
    /**
     * component type for the root node
     */
    component?: any;
    /**
     * height of the skeleton
     */
    height?: SkeletonHeight;
    /**
     * width of the skeleton
     */
    width?: SkeletonWidth;
    /**
     * skeleton animation
     */
    animation?: SkeletonAnimation;
    /**
     * margin top for the skeleton
     */
    marginTop?: number;
}

declare const Skeleton: React.FC<SkeletonProps>;

export default Skeleton;

