import * as React from 'react';

export type SliderCurrencyFormat = "CAD-EN" | "CAD-FR" | "ES-MXN" | "ES-PEN" | "ES-COL" | "ES-CL";

export interface SliderCurrencyConfig {
    prefix?: string;
    suffix?: string;
    thousandsSeparatorSymbol: string;
}

export interface SliderProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Optional input id
     */
    id?: string;
    /**
     * Optional input and slider value
     */
    defaultValue?: number;
    /**
     * Optional label
     */
    label?: string;
    /**
     * Set percentage format
     */
    percentage?: boolean;
    /**
     * Required min value
     */
    min?: any;
    /**
     * Required max value
     */
    max?: any;
    /**
     * Optional onChange function
     */
    onChange?: (...args: any[])=>any;
    /**
     * Optional disabled property
     */
    disabled?: boolean;
    /**
     * Optional track step number forward or back
     */
    step?: number;
    /**
     * Optional aria-label for slider
     */
    ariaLabel?: string;
    /**
     * Optional aria-valuetext for slider
     */
    ariaValueText?: string;
    /**
     * Currency format, currencyFormat prop will always override currencyConfig prop
     */
    currencyFormat?: SliderCurrencyFormat;
    /**
     * Optional config for custom currency
     */
    currencyConfig?: SliderCurrencyConfig;
}

declare const Slider: React.FC<SliderProps>;

export default Slider;

