import * as React from 'react';

export interface SnackBarButton {
    text: string;
    Icon: (...args: any[])=>any;
    onClick: (...args: any[])=>any;
}

export interface SnackBarProps {
    /**
     * Content inside SnackBar
     */
    children: React.ReactNode;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * Time quantity in miliseconds to hide the SnackBar automatically
     */
    duration?: number;
    /**
     * A value to open the Snackbar
     */
    open?: boolean;
    /**
     * Callback fired when the component requests to be closed
     */
    onClose?: (...args: any[])=>any;
    /**
     * A Button to appear in the SnackBar
     */
    button?: SnackBarButton;
}

declare const SnackBar: React.FC<SnackBarProps>;

export default SnackBar;

