import * as React from 'react';

export interface SubmitButtonProps {
    /**
     * The text to appear in the button
     */
    children?: string;
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * No user action on button is allowed
     */
    disabled?: boolean;
    /**
     * Whether or not to use the loading icon (loading variant)
     */
    loading?: boolean;
    /**
     * onClick callback on the button
     */
    onClick?: (...args: any[])=>any;
    /**
     * id on the inner button span. May be needed for some analytics solutions
     */
    spanId?: string;
}

declare const SubmitButton: React.FC<SubmitButtonProps>;

export default SubmitButton;

