import * as React from 'react';

export interface TabProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * State of the current tab
     */
    isActive?: boolean;
    /**
     * Label to show in the tab
     */
    label: string;
    /**
     * Callback to execute when the tab is clicked
     */
    onClick?: (...args: any[])=>any;
    /**
     * Represent the badge as indicator
     */
    notificationBadgeNumber?: number;
    /**
     * Internal current active index passed on by Tabs parent
     */
    currentIndex?: number;
    /**
     * Content inside the Tab.
     */
    children?: React.ReactNode;
}

declare const Tab: React.FC<TabProps>;

export default Tab;

