import * as React from 'react';

export interface TabsProps {
    /**
     * Optional classes can be added
     */
    className?: string;
    /**
     * The children have the Tabs or Filter Dropdown content
     */
    children?: React.ReactNode;
    /**
     * Dropdown label to show when the option is not selected in Filter component
     */
    dropDownLabel?: string;
    /**
     * Callback to execute when the tab is clicked
     */
    onClick?: (...args: any[])=>any;
    /**
     * Optional selection of active tab for controlled component usage
     */
    active?: number;
}

declare const Tabs: React.FC<TabsProps>;

export default Tabs;

